"use strict";

const prompt = require("prompt");
[ // For methods that accept a callback as their argument
	"addProperties",
	"confirm",
	"get",
	"getInput",
	"_performValidation"
].forEach(patching_method => {
	const unpatched_method = prompt[patching_method];
	prompt[patching_method] = function() {
		if(typeof Array.from(arguments).pop() === "function")
		{
			return unpatched_method(...arguments);
		}
		else
		{
			return new Promise((resolve, reject) => {
				unpatched_method(...arguments, (error, result) => {
					if(error)
					{
						reject(error);
						return;
					}
					resolve(result);
				});
			});
		}
	};
});
module.exports = prompt;
